import React, { Component } from "react";

import axios from "axios";

class Door extends Component {
  userId = this.props.match.params.id;
  state = {
    door: {},
    users: [],
  };
  componentDidMount() {
    this.fetchDoorData();
  }
  render() {
    return (
      <div class="card text-white bg-dark mb-3" style="max-width: 18rem;">
        <div class="card-header">Header</div>
        <div class="card-body">
          <h5 class="card-title">Dark card title</h5>
          <p class="card-text">
            Some quick example text to build on the card title and make up the
            bulk of the card's content.
          </p>
        </div>
      </div>
    );
  }

  async fetchDoorData() {
    try {
      const result = await axios.get(
        `http://127.0.0.1:5000/doors/${this.userId}`
      );
      if (result.status === 200) {
        const currentDoor = result.data.door[0];
        const door = {
          id: currentDoor[0],
          name: currentDoor[1],
          dateTime: currentDoor[2],
          address: `${currentDoor[4]}, ${currentDoor[5]}, ${currentDoor[6]}, ${currentDoor[7]}, ${currentDoor[8]}`,
        };
        const users = result.data.users.map((user) => ({
          id: user[0],
          email: user[1],
          firstName: user[2],
          lastName: user[3],
          createdAt: user[4],
        }));
        this.setState({ door, users });
      }
    } catch (error) {
      console.log("Could not fetch doors at this time:", error);
    }
  }
}

export default Door;
