import react from "react";
import React, { Component } from "react";

class Doors extends Component {
  state = {
    alldoors: this.fetchData(),
    //[{ id: 0, name: "front Door", address: "Islamabad", time: "10pm" }, ],
  };
  render() {
    const doors = this.state.alldoors;
    if (doors.length === 0)
      return (
        <react.Fragment>
          <h2>No Doors to display</h2>
          <h2>byee</h2>
        </react.Fragment>
      );
    return (
      <div>
        <h2>Showing {doors.length} movies from Database</h2>
        <table className="table">
          <thead>
            <tr>
              <th>Name</th>
              <th>Address</th>
              <th>Time</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {doors.map((d) => (
              <tr key={d._id}>
                <th>{d.name}</th>
                <th>{d.address}</th>
                <th>{d.time}</th>

                <th>
                  <button
                    onClick={() => this.handleDetail(d._id)}
                    className="btn btn-danger"
                  >
                    Details
                  </button>
                </th>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  }
}

function getMovies() {
  const requestOptions = {
    method: "GET",
    header: { "Content-Type": "application/json" },
  };
  const data = fetch("http://127.0.0.1:5000/get_data", requestOptions)
    .then((response) => response.json())
    .map((item) => ({
      id: item[0],
      name: item[2],
      address: item[3],
      time: item[4],
    }));
  console.log(data);
  return data;
}

export default Doors;
