import react from "react";
import React, { Component } from "react";
import axios from "axios";

class Doors extends Component {
  state = {
    alldoors: [],
    //[{ id: 0, name: "front Door", address: "Islamabad", time: "10pm" }, ],
  };
  componentDidMount() {
    this.fetchData();
  }
  render() {
    const doors = this.state.alldoors;
    if (doors.length === 0)
      return (
        <react.Fragment>
          <h2>No Doors to display</h2>
        </react.Fragment>
      );
    return (
      <div>
        <h2>Showing {doors.length} Door from Database</h2>
        <table className="table">
          <thead>
            <tr>
              <th>Name</th>
              <th>Address</th>
              <th>Time</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {doors.map((d) => (
              <tr key={d.id + d.name}>
                <th>{d.name}</th>
                <th>{d.address}</th>
                <th>{d.dateTime}</th>

                <th>
                  <button
                    onClick={() => this.handleDetail(d._id)}
                    className="btn btn-danger"
                  >
                    Details
                  </button>
                </th>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  }
  async fetchData() {
    try {
      const result = await axios.get("http://127.0.0.1:5000/doors");
      if (result.status === 200) {
        const alldoors = result.data.map((door) => ({
          id: door[0],
          name: door[1],
          dateTime: door[2],
          address: `${door[4]}, ${door[5]}, ${door[6]}, ${door[7]}, ${door[8]}`,
        }));
        this.setState({ alldoors });
      }
    } catch (error) {
      console.log("Could not fetch doors at this time:", error);
    }
  }
}

export default Doors;
