import React, { Component, Fragment } from "react";
import { Link } from "react-router-dom";

import axios from "axios";

class Door extends Component {
  userId = this.props.match.params.id;
  state = {
    door: {},
    users: [],
  };
  componentDidMount() {
    this.fetchDoorData();
  }
  render() {
    return (
      <Fragment>
        <div className="container m-3">
          <div className="card border-info mb-3">
            <div className="card-header" style={{ fontWeight: "bold" }}>
              {this.state.door.name}
            </div>
            <div className="card-body">
              <p className="card-text">
                ID: {this.state.door.id}
                <br />
                Sensor UUID: {this.state.door.sensor_uuid}
                <br />
                Address: {this.state.door.address}
                <br />
                Date Time: {this.state.door.dateTime}
              </p>
            </div>
          </div>
        </div>

        <div className="container m-3">
          <div className="card border-info mb-3">
            <div className="card-header" style={{ fontWeight: "bold" }}>
              Permitted Users
            </div>
            <div className="card-body">
              <table className="table table-bordered table-hover">
                <thead>
                  <tr className="table-info">
                    <th>S.No</th>
                    <th>Name</th>
                    <th>Address</th>
                    <th>Time</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {this.state.users.map((u, index) => (
                    <tr key={d.id + d.name}>
                      <th>{index + 1}</th>
                      <th>{d.name}</th>
                      <th>{d.address}</th>
                      <th>{d.dateTime}</th>

                      <th>
                        <Link to={`./door/${d.id}`}>Detail</Link>
                      </th>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </Fragment>
    );
  }

  async fetchDoorData() {
    try {
      const result = await axios.get(
        `http://127.0.0.1:5000/doors/${this.userId}`
      );
      if (result.status === 200) {
        const currentDoor = result.data.door[0];
        const door = {
          id: currentDoor[0],
          sensor_uuid: currentDoor[1],
          name: currentDoor[2],
          dateTime: currentDoor[4],
          address: `${currentDoor[6]}, ${currentDoor[7]}, ${currentDoor[8]}, ${currentDoor[9]}, ${currentDoor[10]}, ${currentDoor[11]}`,
        };
        const users = result.data.users.map((user) => ({
          id: user[0],
          email: user[1],
          name: `${user[2]} ${user[3]}`,
          createdAt: user[4],
        }));
        this.setState({ door, users });
      }
    } catch (error) {
      console.log("Could not fetch doors at this time:", error);
    }
  }
}

export default Door;
